files.styles.css:
body {
    font-family: 'Roboto', sans-serif;
    margin: 0;
    padding: 0;
    background: linear-gradient(135deg, #0e0b16, #1c1a29);
    color: #e0e0e0;
}

header {
    background-color: #1f1b24;
    padding: 20px;
    text-align: center;
    border-bottom: 2px solid #ff2d55;
}

h1 {
    color: #ff2d55;
    font-size: 3em;
    text-shadow: 0 0 10px #ff2d55, 0 0 20px #ff2d55;
    animation: neon-glow 1.5s infinite alternate;
}

h2 {
    color: #ff6f61;
    text-shadow: 0 0 5px #ff6f61;
    font-size: 2em;
    margin-bottom: 10px;
    border-bottom: 1px solid #2e2a33;
    padding-bottom: 10px;
}

section {
    padding: 20px;
    margin: 20px;
    border: 2px solid #2e2a33;
    border-radius: 10px;
    background: rgba(255, 255, 255, 0.05);
    transition: background 0.3s ease, transform 0.3s ease;
    box-shadow: 0 0 20px rgba(255, 255, 255, 0.1);
}

section:hover {
    background: rgba(255, 255, 255, 0.1);
    transform: scale(1.05);
    box-shadow: 0 0 30px rgba(255, 255, 255, 0.2);
}

ul {
    list-style: none;
    padding: 0;
}

li {
    margin: 10px 0;
    font-size: 1.2em;
    color: #ff9f1c;
    position: relative;
    padding-left: 20px;
}

li:before {
    content: '\2022';
    position: absolute;
    left: 0;
    color: #ff2d55;
    font-size: 1.5em;
    line-height: 1em;
}

.logo {
    width: 150px;
    height: auto;
    margin-top: 10px;
    border: 2px solid #ff2d55;
    border-radius: 50%;
    transition: transform 0.3s ease;
}

.logo:hover {
    transform: rotate(360deg);
}

.about-image {
    width: 100%;
    height: auto;
    margin: 20px 0;
    border: 2px solid #ff6f61;
    box-shadow: 0 0 20px rgba(255, 111, 97, 0.5);
}

@keyframes neon-glow {
    from {
        text-shadow: 0 0 10px #ff2d55, 0 0 20px #ff2d55, 0 0 30px #ff2d55, 0 0 40px #ff2d55;
    }
    to {
        text-shadow: 0 0 20px #ff2d55, 0 0 30px #ff2d55, 0 0 40px #ff2d55, 0 0 50px #ff2d55;
    }
}

